var db  =  {}
module.exports = {
	save:function(AggreTypeName,data,cb){
		
		db[data.id] = data;
		cb()
	},
	update:function(){
		this.save.apply(this,arguments);
	},
	remove:function(AggreTypeName,id,cb){
		delete db[id];
		cb();
	},
	get:function(AggreTypeName,id,cb){
		cb(db[id]);
	},
	all:function(cb){
		cb(db)
	}
}